import style from "./Card.module.css"
import { Link } from "react-router-dom";
import {addFav, removeFav} from '../../redux/Actions/actions'
import {connect} from "react-redux"
import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHexagonXmark } from '@fortawesome/free-solid-svg-icons'

export function Card(props) {
console.log(props.myFavorites);

const {pathname} = useLocation();
const [isFav, setIsfav] = useState(false);

const handleFavorite = () => {
   if (isFav) {
      setIsfav(false);
      props.removeFav(props.id);
   } else {
      setIsfav(true);
      props.addFav(props);
   }
};

useEffect(() => {
    props.myFavorites.forEach((fav) => {
      if (fav.id === props.id) {
         setIsfav(true);
      }
   });
}, [props.myFavorites]);

   return (
      <div className = {style.containerCard}>
         <div>
         { (pathname !== "/favorites") && (<div className = {style.btnClose}  onClick={() => props.onClose() }><FontAwesomeIcon icon={faHexagonXmark} /></div>) }

         {(isFav)? ( <button className = {style.btnFavorites} onClick={handleFavorite}>‚ù§Ô∏è</button>) : (<button className = {style.btnFavorites} onClick={handleFavorite}>ü§ç</button> )}
         </div>
         <img src={props.image} alt='' />
         <Link to={`/detail/${props.id}`}><h2>{props.name}</h2></Link>
         <h2>{props.species}</h2>

      </div>
   );
}

export function mapDispatchToProps(dispatch) {
   return {
      addFav: (personaje) => dispatch(addFav(personaje)),
      removeFav: (id) => dispatch(removeFav(id))
   }
}

export function mapStateToProps(state) {
   return {
      myFavorites: state.myFavorites,
   }
}

export default connect(mapStateToProps, mapDispatchToProps)(Card);
