import './App.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
//import characters, { Rick } from './data.js';
import About from './components/About/About';
import Detail from './components/Detail/Detail';
import React, { useState } from 'react';
import {Routes, Route} from "react-router-dom";

function App() {

let [characters, setCharacters] = useState([]);

const onSearch = (id) => {

      console.log(characters);
      //characters.map((elemento,i) => console.log(elemento.id));

      if (characters.id === parseInt(id)) {
         window.alert('El personaje ya esta repetido');
      } else {
         fetch(`https://rickandmortyapi.com/api/character/${id}`)
         .then((res) => res.json())
         .then((data) => setCharacters([...characters,data]))
      }


};

const onClose = (id) => {
   setCharacters(characters.filter((elemento) => elemento.id !== parseInt(id)));
};


   return (
      <div className='App'>
      <Nav onSearch={onSearch} />
      <Routes>
         <Route path='/home' element={<Cards />} />
         <Route path='/about' element={<About />} />
         <Route path='/detail/:id' element={<Detail />} />      
      </Routes>
      </div>
   );
}

export default App;

