import Form from "./components/Form/Form";

const validation = () => {

const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
let errors = {};
    if(!regexEmail.test(Form.email)) errors.email = "Debe ser un correo electrónico valido";
    if(!Form.email) errors.email = "Se requiere un correo electrónico";

    if(!Form.password) errors.email = "Se requiere un correo electrónico";
    return errors;

}

export default validation
