import { useSelector, useDispatch } from "react-redux"
import Card from "../Card/Card";
import style from "./Favorites.module.css";
import { orderCards, filterCards } from '../../redux/actions/actions'

export default function Favorites() {

   const { myFavorites } = useSelector(state => state);
   
   const dispatch = useDispatch();

   const handleOrder = (event) =>{
      dispatch(orderCards(event.target.value))
   }

   const handleFilter = (event) =>{
      dispatch(filterCards(event.target.value))
   }

   return (
   <div>
      <div className={style.textFavoritos}>
            <h1>
               Favoritos
            </h1>
      </div>
      <div className={style.filterOrder}>
         <select onChange={handleOrder}>
            <option value='order' disabled='disabled'>Order By</option>
            <option value="A">Ascendente</option>
            <option value="D">Descendiente</option>
         </select>
         <div className={style.dropdown}>
            <input type="text" className={style.textBox} placeholder='Dropdown Menu' readOnly onChange={handleFilter}></input>
            <div className={style.options}>
               <div value="All">All</div>
               <div value="Male"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-gender-male" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M9.5 2a.5.5 0 0 1 0-1h5a.5.5 0 0 1 .5.5v5a.5.5 0 0 1-1 0V2.707L9.871 6.836a5 5 0 1 1-.707-.707L13.293 2H9.5zM6 6a4 4 0 1 0 0 8 4 4 0 0 0 0-8z"/></svg>  
                Male </div>
               <div value="Female"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-gender-female" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M8 1a4 4 0 1 0 0 8 4 4 0 0 0 0-8zM3 5a5 5 0 1 1 5.5 4.975V12h2a.5.5 0 0 1 0 1h-2v2.5a.5.5 0 0 1-1 0V13h-2a.5.5 0 0 1 0-1h2V9.975A5 5 0 0 1 3 5z"/></svg> 
               Female </div>
               <div value="Genderless">Genderless</div>
               <div value="unknown"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-question" viewBox="0 0 16 16"><path d="M5.255 5.786a.237.237 0 0 0 .241.247h.825c.138 0 .248-.113.266-.25.09-.656.54-1.134 1.342-1.134.686 0 1.314.343 1.314 1.168 0 .635-.374.927-.965 1.371-.673.489-1.206 1.06-1.168 1.987l.003.217a.25.25 0 0 0 .25.246h.811a.25.25 0 0 0 .25-.25v-.105c0-.718.273-.927 1.01-1.486.609-.463 1.244-.977 1.244-2.056 0-1.511-1.276-2.241-2.673-2.241-1.267 0-2.655.59-2.75 2.286zm1.557 5.763c0 .533.425.927 1.01.927.609 0 1.028-.394 1.028-.927 0-.552-.42-.94-1.029-.94-.584 0-1.009.388-1.009.94z"/></svg> 
               unknown</div>
            </div>
         </div>
         {/* <select onChange={handleFilter}>
            <option value='filter' disabled='diseabled'>Filter By</option>
            <option value="All">All</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
         </select> */}
      </div>
      <div className={style.cardContainer}>
         {  
          myFavorites.map((elemento) => {  
           return <Card
            id={elemento.id}
            name={elemento.name}
            image={elemento.image}
            />
        })} 
   </div> 
   </div>  
   );
}
