import React from 'react'
import { useState } from 'react'
import validation from '../../validation'
import style from "./Form.modules.css"

export default function Form () { 

const [userData,setUsedata] = useState({
    email:"",
    password:"",
});

const [errors,setErrors] = useState({
    email:"",
    password:"",
});


const handleChange = (event) => {
    setErrors(validation({...userData,[event.target.name]:event.target.value}));
    setUsedata({...userData,[event.target.name]:event.target.value});  
};

  return (
    <div>
      <form><br />
        <label>Email:</label>
        <input type="text" 
               name="email" 
               value={userData.email} 
               onChange={handleChange}
               placeholder='Escribe tu email...' /><br /><br />
        <p className={style.danger}>{errors.email}</p>
        <label>Password:</label>
        <input type="text" 
               name="password" 
               value={userData.password} 
               onChange={handleChange}
               placeholder='Escribe tu password...' /><br /><br />
        <p className={style.danger}>{errors.password}</p>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
